import randomimport colorsysfrom statistics import euclidean_distancedef lazy_generator(colors):	last_color = ()	new_color = ()	if len(colors) > 0:		last_color = colors[len(colors) - 1]		r, g, b = last_color		b = b + 1		if b == 256:			b = b % 256			g = g + 1		if g == 256:			g = g % 256			r = (r + 1) % 256		new_color = r, g, b	else:		new_color = (0, 0, 0)	return new_color	def color_generator(colors):	bool_check = False	if type(colors) == list:		if len(colors) > 0:			bool_check = True	newcolor = new_color()	if bool_check:		bool_distance = color_is_distinct(colors, newcolor)		while bool_distance:			newcolor = new_color()			bool_distance = color_is_distinct(colors, newcolor)	return newcolor	#http://en.wikipedia.org/wiki/HSL_and_HSVdef new_color():	min_value_lightness, max_value_lightness = 0.1, 0.5	min_value_saturation, max_value_saturation = 0.1, 1.0	rgb_max_value = 255	h = (random.uniform(0, 360))/360 #hue	l = random.uniform(min_value_lightness, max_value_lightness) #lightness	s = random.uniform(min_value_saturation, max_value_saturation) #saturation	r, g, b = colorsys.hls_to_rgb(h, l, s)	r = int(r * rgb_max_value)	g = int(g * rgb_max_value)	b = int(b * rgb_max_value)	return (r, g, b)def color_is_distinct(colors, newcolor):	bool_distance = False	for color in colors:		distance = euclidean_distance(color, newcolor)		if distance < 50:			bool_distance = True			break	return bool_distance